import React from "react";
import Card from "react-bootstrap/Card";
import styles from "./WeatherDisplay.module.css"; // Ensure the CSS module is correctly linked

const WeatherDisplay = ({ weather }) => {
  // Helper function to determine weather emoji based on description
  const getWeatherEmoji = (description) => {
    description = description.toLowerCase();
    if (description.includes("cloud")) return "‚òÅÔ∏è";
    if (description.includes("rain")) return "üåßÔ∏è";
    if (description.includes("clear")) return "‚òÄÔ∏è";
    if (description.includes("snow")) return "‚ùÑÔ∏è";
    if (description.includes("thunderstorm")) return "‚õàÔ∏è";
    return "üåç"; // Fallback for other conditions
  };

  // Additional emojis based on the weather conditions
  const getHumidityEmoji = (humidity) => {
    if (humidity > 80) return "üí¶";
    if (humidity < 30) return "üåµ";
    return "üå´Ô∏è"; // Moderate humidity
  };

  const getWindEmoji = (speed) => {
    if (speed > 25) return "üå¨Ô∏è"; // High wind
    if (speed > 10) return "üçÉ"; // Moderate wind
    return "üåÄ"; // Low wind
  };

  const getPressureEmoji = (pressure) => {
    if (pressure > 1020) return "üîº"; // High pressure
    if (pressure < 1000) return "üîΩ"; // Low pressure
    return "‚èπÔ∏è"; // Normal pressure
  };

  return (
    <Card border="secondary" style={{ width: "18rem" }}>
      <Card.Header className={styles.weatherHeader}>
        Weather Details
      </Card.Header>
      <Card.Body>
        {weather ? (
          <>
            <Card.Text>Temperature: {weather.main.temp}¬∞C</Card.Text>
            <Card.Text>
              Description: {weather.weather[0].description}{" "}
              {getWeatherEmoji(weather.weather[0].description)}
            </Card.Text>
            <Card.Text>
              Humidity: {weather.main.humidity}%{" "}
              {getHumidityEmoji(weather.main.humidity)}
            </Card.Text>
            <Card.Text>
              Wind Speed: {weather.wind.speed} km/h{" "}
              {getWindEmoji(weather.wind.speed)}
            </Card.Text>
            <Card.Text>
              Pressure: {weather.main.pressure} hPa{" "}
              {getPressureEmoji(weather.main.pressure)}
            </Card.Text>
          </>
        ) : (
          <Card.Text>No weather data available.</Card.Text>
        )}
      </Card.Body>
    </Card>
  );
};

export default WeatherDisplay;
